) ENGINE=InnoDB AUTO_INCREMENT=10 DEFAULT CHARSET=utf8;
delimiter ;;
CREATE TRIGGER `dmand_insert_trigger` AFTER INSERT ON `demand` FOR EACH ROW BEGIN
SELECT demand_count into @demand_count from user_evendata where user_evendata.uid = new.uid;
set @demand_count = @demand_count + 1;
UPDATE user_evendata SET user_evendata.demand_count=@demand_count where user_evendata.uid=new.uid;
END;
 ;;
delimiter ;
delimiter ;;
CREATE TRIGGER `demand_delete_trigger` AFTER DELETE ON `demand` FOR EACH ROW BEGIN
SELECT demand_count into @demand_count from user_evendata where user_evendata.uid = old.uid;
set @demand_count = @demand_count - 1;
if @demand_count < 1  THEN
UPDATE user_evendata SET user_evendata.demand_count=0 where user_evendata.uid=old.uid;
ELSE
UPDATE user_evendata SET user_evendata.demand_count=@demand_count where user_evendata.uid=old.uid;
end if;
END;
 ;;
delimiter ;

