delimiter ;;
CREATE TRIGGER `dynamic_insert_triggers` AFTER INSERT ON `dynamic` FOR EACH ROW BEGIN
SELECT dynamic_count into @dynamic_count from user_evendata where user_evendata.uid=new.uid;
set @dynamic_count = @dynamic_count + 1;
UPDATE user_evendata SET user_evendata.dynamic_id=new.id,user_evendata.dynamic_count=@dynamic_count where user_evendata.uid=new.uid;
END;
 ;;
delimiter ;
delimiter ;;
CREATE TRIGGER `dynamic_update_trigger` AFTER UPDATE ON `dynamic` FOR EACH ROW BEGIN
UPDATE user_evendata SET user_evendata.dynamic_id=new.id where user_evendata.uid=new.uid;
END;
 ;;
delimiter ;
delimiter ;;
CREATE TRIGGER `dynamic_delete_trigger` AFTER DELETE ON `dynamic` FOR EACH ROW BEGIN
SELECT dynamic_count into @dynamic_count from user_evendata where user_evendata.uid = old.uid;
set @dynamic_count = @dynamic_count - 1;
if @dynamic_count < 1  THEN
UPDATE user_evendata SET user_evendata.dynamic_id=null,user_evendata.dynamic_count=0 where user_evendata.uid=old.uid;
ELSE
SELECT id INTO @dynamic_id from dynamic where uid = old.uid ORDER BY id DESC;
UPDATE user_evendata SET user_evendata.dynamic_id=@dynamic_id,user_evendata.dynamic_count=@dynamic_count where user_evendata.uid=old.uid;
end if;
END;
 ;;
