  `tid` int(11) NOT NULL COMMENT '关注用户编号',
  PRIMARY KEY (`id`),
  KEY `uid` (`uid`),
  KEY `tid` (`tid`),
  CONSTRAINT `user_attention_ibfk_1` FOREIGN KEY (`uid`) REFERENCES `user_account` (`uid`),
