) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='关注表';
delimiter ;;
CREATE TRIGGER `user_attention_insert_trigger` AFTER INSERT ON `user_attention` FOR EACH ROW BEGIN
SELECT attention_count into @attention_count from user_evendata where user_evendata.uid = new.uid;
SELECT fans_count into @fans_count from user_evendata where user_evendata.uid = new.tid;
set @attention_count = @attention_count + 1;
set @fans_count = @fans_count + 1;
UPDATE user_evendata SET user_evendata.attention_count=@attention_count where user_evendata.uid=new.uid;
UPDATE user_evendata SET user_evendata.fans_count=@fans_count where user_evendata.uid=new.tid;
END;
 ;;
delimiter ;
delimiter ;;
CREATE TRIGGER `user_attention_delete_trigger` AFTER DELETE ON `user_attention` FOR EACH ROW BEGIN
SELECT attention_count into @attention_count from user_evendata where user_evendata.uid = old.uid;
set @attention_count = @attention_count - 1;
if @attention_count < 1  THEN
UPDATE user_evendata SET user_evendata.attention_count=0 where user_evendata.uid = old.uid;
ELSE
UPDATE user_evendata SET uuser_evendata.attention_count=@attention_count where user_evendata.uid = old.uid;
end if;

SELECT fans_count into @fans_count from user_evendata where user_evendata.uid = old.tid;
set @fans_count = @fans_count - 1;
if @fans_count < 1  THEN
UPDATE user_evendata SET user_evendata.fans_count=0 where user_evendata.uid = old.tid;
ELSE
UPDATE user_evendata SET uuser_evendata.fans_count=@fans_count where user_evendata.uid = old.tid;
end if;

END;
 ;;
delimiter ;

